import { useEffect, useState } from "react"
import { CategorieItem } from "../components/CategorieItem";
import './EnchereItem.css'

export function CRUDCategorie(){
    const [nomCategorie, setNomCategorie] = useState();
    const [listeCategorie, setListeCategorie] = useState();

    function addEnchere() {
        fetch("https://venteauxenchere-backoffice-production.up.railway.app/categories",{
            method:'POST',
            headers:{
                "Content-type":"application/json",
                "userId":localStorage.getItem("userId"),
                "hash":localStorage.getItem("hash")
            },
            body:JSON.stringify({"nomCategorie":nomCategorie})
        }).then((result)=>{
            return result.json();
        }).then((e)=>{
            if(e.error !== undefined){
                console.log(e.error);
                // setError("Aucun resultat");
            } else {
                getListeCategorie();
            }
        },(e)=>{
            console.log(e);
        });
    }

    function getListeCategorie() {
        fetch("https://venteauxenchere-backoffice-production.up.railway.app/categories",{
            method:'GET',
            headers:{
                "userId":localStorage.getItem("userId"),
                "hash":localStorage.getItem("hash")
            },
        }).then((result)=>{
            return result.json();
        }).then((e)=>{
            if(e.error !== undefined){
                console.log(e.error);
            } else {
                let data = e.data;
                setListeCategorie(data);
            }
        },(e)=>{
            console.log(e);
        });
    }

    useEffect(()=>{
        getListeCategorie();
    },[]);

    return(
        <div>
            <h2>Les Categories</h2>
            <div className="row">
                <div class="card shadow col-md-3" style={{marginLeft:"1%"}}>
                    <div className="card-header">
                        <h6 className="text-primary">Ajout Categorie</h6>
                    </div>
                    <div class="card-body">
                        <div>
                            <form action="">
                                <div className="form-group">
                                    <label>Nom Categorie</label>
                                    <input type="text" className="form-control" name="nomCategorie" onChange={(e)=>{setNomCategorie(e.target.value)}}/>
                                </div>
                                <br />
                                <div className="form-group">
                                    <input class="btn btn-success form-control" style={{color:"white"}} onClick={(e)=>{addEnchere()}} type="button" value={"Inserer"}/>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
                <div class="card shadow col-md-8">
                    <div className="card-header">
                        <h6 className="text-primary">Liste des categories </h6>
                    </div>
                    <div class="card-body">
                        <table className="table table-stripped">
                        <thead>
                            <tr>
                                <th>idCategorie</th>
                                <th>Nom categorie</th>
                            </tr>
                        </thead>
                            { listeCategorie?.map((value)=><CategorieItem categorie={value} />)}
                        </table>
                    </div>
                </div>
            </div>
        </div>
    )
}
