import { useEffect, useState } from "react"
import { CategorieItem } from "../components/CategorieItem";
import './EnchereItem.css'

export function CRUDCategorie(){
    const [nomCategorie, setNomCategorie] = useState();
    const [listeCategorie, setListeCategorie] = useState();
    const [error, setError] = useState('');

    function addEnchere() {
        alert(nomCategorie);
    }

    function getListeCategorie() {
        fetch("https://venteauxenchere-backoffice-production.up.railway.app/categories",{
            method:'GET',
            headers:{
                "userId":localStorage.getItem("userId"),
                "hash":localStorage.getItem("hash")
            },
        }).then((result)=>{
            return result.json();
        }).then((e)=>{
            if(e.error !== undefined){
                setError("Aucun resultat");
            } else {
                let data = e.data;
                setListeCategorie(data);
                alert(JSON.stringify(listeCategorie));
            }
        },(e)=>{
            setError("No connection");
        });
    }

    useEffect(()=>{
        getListeCategorie();
    },[]);

    return(
        <div>
            <div class="card shadow padding-card w-25 i-flex">
                <h1>Ajout Categorie </h1>
                <div class="card-body">
                    <div>
                        <form action="">
                            <div className="form-group">
                                <label>Nom Categorie</label>
                                <input type="text" className="form-control" name="nomCategorie" onChange={(e)=>{setNomCategorie(e.target.value)}}/>
                            </div>
                            <br />
                            <div className="form-group">
                                <input class="btn btn-secondary form-control" onClick={(e)=>{addEnchere()}} type="button" value={"Inserer"}/>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
            <div class="card shadow padding-card w-50 i-flex">
                <h3>Liste des categories </h3>
                <div class="card-body">
                    <table className="table table-stripped">
                    {/* <thead>
                        <tr>
                            <th>idCategorie</th>
                            <th>Nom categorie</th>
                        </tr>
                    </thead> */}
                        {listeCategorie.map((value)=><CategorieItem categorie={value} />)}
                    </table>
                </div>
            </div>
        </div>
    )
}
