import { useState } from "react";
import { NavBar } from "../components/NavBar";

export default function Commission(){
    const [comission, setComission] = useState();
    const [nomCategorie, setNomCategorie] = useState();


    function getComission() {
        setComission(10);
    }

    function addEnchere() {
        fetch("https://venteauxenchere-backoffice-production.up.railway.app/categories",{
            method:'POST',
            headers:{
                "Content-type":"application/json",
                "userId":localStorage.getItem("userId"),
                "hash":localStorage.getItem("hash")
            },
            body:JSON.stringify({"nomCategorie":nomCategorie})
        }).then((result)=>{
            return result.json();
        }).then((e)=>{
            if(e.error !== undefined){
                console.log(e.error);
                // setError("Aucun resultat");
            } else {
                getListeCategorie();
            }
        },(e)=>{
            console.log(e);
        });
    }

    function addComission() {
        fetch("https://venteauxenchere-backoffice-production.up.railway.app/categories",{
            method:'GET',
            headers:{
                "userId":localStorage.getItem("userId"),
                "hash":localStorage.getItem("hash")
            },
        }).then((result)=>{
            return result.json();
        }).then((e)=>{
            if(e.error !== undefined){
                console.log(e.error);
            } else {
                let data = e.data;
                setListeCategorie(data);
            }
        },(e)=>{
            console.log(e);
        });
    }

    return (
        <div>
            <NavBar/>
            <div>
                <h1>Gestion de commission</h1>
                <form action="">
                    <div className="form-group">
                        <label>Nom Categorie</label>
                        <input type="text" className="form-control" name="nomCategorie" onChange={(e)=>{setNomCategorie(e.target.value)}}/>
                    </div>
                    <br />
                    <div className="form-group">
                        <input class="btn btn-secondary form-control" onClick={(e)=>{addEnchere()}} type="button" value={"Inserer"}/>
                    </div>
                </form>
            </div>
            <div>
                <h1>Gestion de commission</h1>
                <form action="">
                    <div className="form-group">
                        <label>Pourcentage</label>
                        <input type="text" className="form-control" onChange={(e)=>{setComission(e.target.value)}}/>
                    </div>
                    <br />
                    <div className="form-group">
                        <input class="btn btn-secondary form-control" onClick={(e)=>{addEnchere()}} type="button" value={"Inserer"}/>
                    </div>
                </form>
            </div>
        </div>


    )
}
