import { useEffect, useState } from "react";
import { NavBar } from "../components/NavBar";
import { RechargeItem } from "../components/RechargeItem";

export default function ListeDemande(){
    const [listeDemande, setListeDemande] = useState();

    function getListeDemande() {
        const liste = {"data": [
                {
                    "idRechargement": 7,
                    "client": {
                        "idClient": 1,
                        "nom": "Anjara",
                        "prenom": "Joston",
                        "soldeClient": 1200.0,
                        "myToken": {
                            "userId": 1,
                            "hash": "c4ca4238a0b923820dcc509a6f75849b",
                            "expirationDate": "2023-01-20T15:54:18.066+00:00"
                        }
                    },
                    "idClient": 1,
                    "montant": 1200.0,
                    "dateRechargement": "2023-01-20",
                    "etat": 1
                },
                {
                    "idRechargement": 8,
                    "client": {
                        "idClient": 1,
                        "nom": "Anjara",
                        "prenom": "Joston",
                        "soldeClient": 1200.0,
                        "myToken": {
                            "userId": 1,
                            "hash": "c4ca4238a0b923820dcc509a6f75849b",
                            "expirationDate": "2023-01-20T15:54:18.066+00:00"
                        }
                    },
                    "idClient": 1,
                    "montant": 6000.0,
                    "dateRechargement": "2023-01-21",
                    "etat": 1
                }
            ]
        }

        setListeDemande(liste);
    }

    useEffect(()=>{
        getListeDemande();
    },[]);

    return (
        <div>
            <NavBar/>
            <div className="container">
                <div>
                    <h1>Liste des demandes</h1>
                    <div className="row">
                        {listeDemande.data?.map((value)=>{
                            return(
                                <div className="col-md-3 col-sm-5" style={{marginTop:"30px"}}>
                                    <RechargeItem valeur={value} />
                                </div>
                            )
                        })}
                    </div>
                </div>
            </div>
        </div>
    )
}
